import React, {Component} from 'react';
import './App.css';
import Part from './component/particles/particles';
import Navigation from './component/navigation/navigation';
import Logo from './component/logo/logo';
import ImageLinkForm from './component/imagelinkform/imagelinkform';
import Rank from './component/rank/rank';
import FaceRecognize from './component/facerecognize/facerecognize';

//import Teszt from './component/teszt/teszt';




class App extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            imageUrl: '',
            box: {}
        }

    }



    displayFaceBox = (box) => {
        console.log(box);
        this.setState({box: box});
    }

    onInputChange = (event) => {
        this.setState({input: event.target.value});
    }

    onButtonSubmit = () => {
        this.setState({imageUrl: this.state.input});

    }

    onTeszt = () => {
        const PAT = 'e3e2e57c46984160bfd4a496e0abe5ec';

        const USER_ID = 'chrissmith';       
        const APP_ID = 'test';

        const MODEL_ID = 'face-detection';
        const MODEL_VERSION_ID = '6dc7e46bc9124c5c8824be4822abe105';    

        //const IMAGE_URL = {imageUrl: this.state.input};
        const IMAGE_URL = 'https://samples.clarifai.com/metro-north.jpg';        

        const raw = JSON.stringify({
            "user_app_id": {
                "user_id": USER_ID,
                "app_id": APP_ID
            },
            "inputs": [
                {
                    "data": {
                        "image": {
                            "url": IMAGE_URL
                        }
                    }
                }
            ]
        });

        const requestOptions = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Key ' + PAT
            },
            body: raw
        };
        return(
        fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/versions/" + MODEL_VERSION_ID + "/outputs", requestOptions)
        .then(response => response.text())
        .then(response => response.text())
        //.then(response => this.displayFaceBox(this.calculateFaceLocation(response)))
        .then(result => console.log(result))
        .catch(error => console.log('error', error))
        );
    }

    calculateFaceLocation = (data) => {

        const clarifaiFace = data.response.outputs[0].data.regions[0].region_info.bounding_box;
        const image = document.getElementById('inputimage');
        const width = Number(image.width);
        const height = Number(image.height);
        console.log(width, height);
        return {
           leftCol: clarifaiFace.left_col * width,
           topRow: clarifaiFace.top_row * height,
           rightCol: width - (clarifaiFace.right_col * width),
           bottomRow: height - (clarifaiFace.bottom_row * height)
        }   


    }

    render() {
    return (
      <div className="App">
      <Part />
      <Navigation />
      <Logo />
      <Rank />
      <ImageLinkForm 
      onInputChange={this.onInputChange} 
      onButtonSubmit={this.onButtonSubmit} />

      <FaceRecognize imageUrl={this.state.imageUrl} />

      </div>
    )
  }
}

export default App;
